import { PieChart, Pie, Cell, Tooltip } from "recharts";
import { artistData } from "../data/seed";
import { AnyObject } from "../types";
import { useState } from "react";

const Charts = () => {
	const [data, setData] = useState<Array<AnyObject>>(artistData);

	let colors: any = [
		"#2f4f4f",
		"#2e8b57",
		"#8b0000",
		"#808000",
		"#000080",
		"#ff0000",
		"#ff8c00",
		"#7fff00",
		"#ba55d3",
		"#00ffff",
		"#0000ff",
		"#f08080",
		"#ff00ff",
		"#1e90ff",
		"#ffff54",
		"#dda0dd",
		"#ff1493",
		"#f5deb3",
		"#98fb98",
		"#808080",
		"#000000",
	];

	const idiomas = [
		{ name: "Español", value: 58 },
		{ name: "Inglés", value: 221 },
		{ name: "Japonés", value: 4 },
	];

	const data02 = [
		{ name: "Group A", value: 2400 },
		{ name: "Group B", value: 4567 },
		{ name: "Group C", value: 1398 },
		{ name: "Group D", value: 9800 },
		{ name: "Group E", value: 3908 },
		{ name: "Group F", value: 4800 },
	];

	const randomNoRepeats = (arr: any) => {
		var copy = arr.slice(0);
		return function () {
			if (copy.length < 1) {
				copy = arr.slice(0);
			}
			var index = Math.floor(Math.random() * copy.length);
			var item = copy[index];
			copy.splice(index, 1);
			return item;
		};
	};

	return (
		<PieChart width={1000} height={400}>
			<Pie
				dataKey="value"
				isAnimationActive={false}
				data={idiomas}
				cx={200}
				cy={200}
				outerRadius={80}
				fill="#8884d8"
				label
			>
				{idiomas.map((entry, index) => {
                    colors = randomNoRepeats(colors)
                    return <Cell fill={colors[index]} />
                }
				)}
			</Pie>
			<Tooltip />
		</PieChart>
	);
};

export default Charts;
