import { PieChart, Pie, Cell, Tooltip } from "recharts";
import { artistData } from "../data/seed";
import { AnyObject } from "../types";
import { useEffect, useState } from "react";

const Charts = () => {
	const [data, setData] = useState<Array<AnyObject>>(artistData);

	let colors: any = [
		"#2f4f4f",
		"#2e8b57",
		"#8b0000",
		"#808000",
		"#000080",
		"#ff0000",
		"#ff8c00",
		"#7fff00",
		"#ba55d3",
		"#00ffff",
		"#0000ff",
		"#f08080",
		"#ff00ff",
		"#1e90ff",
		"#ffff54",
		"#dda0dd",
		"#ff1493",
		"#f5deb3",
		"#98fb98",
		"#808080",
		"#000000",
	];

	const idiomas = [
		{ name: "Español", value: 58 },
		{ name: "Inglés", value: 221 },
		{ name: "Japonés", value: 4 },
	];

    const RADIAN = Math.PI / 180;
	const sin = Math.sin(-RADIAN * 10);
	const cos = Math.cos(-RADIAN * 10);
	const sx = 30 + (20 + 10) * cos;
	const sy = 30 + (20 + 10) * sin;
	const mx = 30 + (20 + 30) * cos;
	const my = 30 + (20 + 30) * sin;
	const ex = mx + (cos >= 0 ? 1 : -1) * 22;
	const ey = my;
	const textAnchor = cos >= 0 ? "start" : "end";

	const customLabel = (
		<>
			<text
				x={ex + (cos >= 0 ? 1 : -1) * 12}
				y={ey}
				textAnchor="start"
				fill="#333"
			>{`PV ${value}`}</text>
			<text
				x={ex + (cos >= 0 ? 1 : -1) * 12}
				y={ey}
				dy={18}
				textAnchor={textAnchor}
				fill="#999"
			>
				{`(Rate ${(23 * 100).toFixed(2)}%)`}
			</text>
		</>
	);

	return (
		<PieChart width={1000} height={400}>
			<Pie
				dataKey="value"
				isAnimationActive={false}
				data={idiomas}
				cx={200}
				cy={200}
				outerRadius={80}
				fill="#8884d8"
				label
			>
				{idiomas.map((entry, index) => (
					<Cell fill={colors[index % colors.length]} />
				))}
			</Pie>
			<Tooltip />
		</PieChart>
	);
};

export default Charts;
