import {
	Button,
	Modal,
	ModalBody,
	ModalCloseButton,
	ModalContent,
	ModalFooter,
	ModalHeader,
	ModalOverlay,
	useDisclosure,
} from "@chakra-ui/react";
import SpotifyPlayer from "./SpotifyPlayer";

const PlayerModal = (props: any) => {

	const { isOpen, onClose } = props.modalDisclosure;

	return (
		<Modal isOpen={isOpen} onClose={onClose} isCentered border-radius="10%">
			<ModalOverlay />
			<ModalContent>
				<SpotifyPlayer album={props.album} />
			</ModalContent>
		</Modal>
	);
};

export default PlayerModal;
