import { useEffect, useState } from "react";
import { artistData } from "../data/seed";
import { AnyObject } from "../types";
import { Badge, Box, Card, Image, Text, useDisclosure } from "@chakra-ui/react";
import { StarIcon } from "@chakra-ui/icons";
import { Spotify } from "react-spotify-embed";
import PlayerModal from "./PlayerModal";

const AlbumCover = (props: any) => {
	const [data, setData] = useState<Array<AnyObject>>(artistData);
	const [selectedAlbum, setSelectedAlbum] = useState<AnyObject>();

	// modal disclosure
	const {
		isOpen: isOpen,
		onOpen: openPlayer,
		onClose: handleClosePlayer,
	} = useDisclosure();

	//set selected album as a state to pass it along to the modal
	const handleOpenPlayer = (album: AnyObject) => {
		setSelectedAlbum(album);
		openPlayer();
	};

	let res = data.map((current) => (
		<>
			<Box
				key={current.spotifyAlbumId}
				padding="4px"
				onClick={() =>
					handleOpenPlayer({
						spotifyAlbumId: current.spotifyAlbumId,
						isAlbum: current.isAlbum,
					})
				}
			>
				<Image src={current.coverArt} alt={current.coverArt} />
				<Text fontSize="md">{current.album}</Text>
				<Text fontSize="sm">{current.artist}</Text>
			</Box>
			<PlayerModal key={current.spotifyAlbumId} album={selectedAlbum} modalDisclosure={{ isOpen: isOpen, onOpen: handleOpenPlayer, onClose: handleClosePlayer }} />
		</>
	));

	//pa que typescript no joda
	return <>{res}</>;
};

export default AlbumCover;
