import {
	Table,
	TableContainer,
	Tbody,
	Td,
	Tfoot,
	Th,
	Thead,
	Tr,
} from "@chakra-ui/react";
import React from "react";
import { TriangleDownIcon, TriangleUpIcon } from "@chakra-ui/icons";
import { useEffect, useState } from "react";

const ArtistTable = () => {
	const [artists, setArtists] = useState<AnyObject>();

	const url =
		"https://sheets.googleapis.com/v4/spreadsheets/1ihTMg293oX-0F5ujBPRkN2rfCg9o1l1yLZWeBgW9rSw/values/Sheet1!A:H?majorDimension=ROWS&key=AIzaSyC8KA4Chg1gzz6mvhp-4pH5mE03b49Js5U";
	const fetchData = async (controller: AbortController) => {
		const resp = await fetch(url, {
			signal: controller.signal,
		});

		if (!resp.ok) throw new Error("Explotó");

		const respData = await resp.json();
		setArtists(respData);
	};

	useEffect(() => {
		let controller = new AbortController();

		fetchData(controller);

		return () => controller?.abort();
	}, []);

	let list = artists?.values.slice(1).map((current: string, index: number) => (
		<Tr key={current[7]}>
			<Td isNumeric>{index + 1}</Td>
			<Td>{current[0]}</Td>
			<Td>{current[1]}</Td>
			<Td>{current[2]}</Td>
			<Td isNumeric>{current[3]}</Td>
			<Td>{current[4]}</Td>
			<Td>{current[5]}</Td>
			<Td isNumeric>{current[6]}</Td>
		</Tr>
	));

	const columns = ["#", "Artist", "Album", "Genre", "Year", "Language", "Location", "Purchased"]

	return (
		<TableContainer>
			<Table variant="striped" size="sm">
				<Thead>
					<Tr>
						{/* <Th isNumeric>#</Th>
						<Th>Artist</Th>
						<Th>Album</Th>
						<Th>Genre</Th>
						<Th isNumeric>Year</Th>
						<Th>Language</Th>
						<Th>Location</Th>
						<Th isNumeric>Purchased</Th> */}
						{columns.map((column) => (
							<Th key={column} onClick={handleSort}>
								{column}
								<span style={{ paddingLeft: 4 + "px" }}>
									{sortField === column ? (
										sortOrder === "asc" ? (
											<TriangleUpIcon />
										) : (
											<TriangleDownIcon />
										)
									) : null}
								</span>
							</Th>
						))}
					</Tr>
				</Thead>
				<Tbody>{list}</Tbody>
			</Table>
		</TableContainer>
	);
};

interface AnyObject {
	[key: string]: any;
}

export default ArtistTable;
