import { artistData } from "../data/seed";
import { AnyObject } from "../types";
import { useEffect, useState } from "react";
import { ResponsivePie } from "@nivo/pie";

const Charts = () => {
	const [data, setData] = useState<Array<AnyObject>>(artistData);

	let colors: any = [
		"#2f4f4f",
		"#2e8b57",
		"#8b0000",
		"#808000",
		"#000080",
		"#ff0000",
		"#ff8c00",
		"#7fff00",
		"#ba55d3",
		"#00ffff",
		"#0000ff",
		"#f08080",
		"#ff00ff",
		"#1e90ff",
		"#ffff54",
		"#dda0dd",
		"#ff1493",
		"#f5deb3",
		"#98fb98",
		"#808080",
		"#000000",
	];

	const idiomas = [
        {
          "id": "sass",
          "label": "sass",
          "value": 265,
          "color": "hsl(169, 70%, 50%)"
        },
        {
          "id": "go",
          "label": "go",
          "value": 141,
          "color": "hsl(180, 70%, 50%)"
        },
        {
          "id": "java",
          "label": "java",
          "value": 480,
          "color": "hsl(131, 70%, 50%)"
        },
        {
          "id": "make",
          "label": "make",
          "value": 272,
          "color": "hsl(74, 70%, 50%)"
        },
        {
          "id": "stylus",
          "label": "stylus",
          "value": 68,
          "color": "hsl(312, 70%, 50%)"
        }
      ]

	return (
		<ResponsivePie
			data={idiomas}
			margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
			// innerRadius={0.5}
			padAngle={0.7}
			cornerRadius={3}
			activeOuterRadiusOffset={8}
			borderWidth={1}
			borderColor={{
				from: "color",
				modifiers: [["darker", 0.2]],
			}}
			arcLinkLabelsSkipAngle={10}
			arcLinkLabelsTextColor="#333333"
			arcLinkLabelsThickness={2}
			arcLinkLabelsColor={{ from: "color" }}
			arcLabelsSkipAngle={10}
			arcLabelsTextColor={{
				from: "color",
				modifiers: [["darker", 2]],
			}}
			defs={[
				{
					id: "dots",
					type: "patternDots",
					background: "inherit",
					color: "rgba(255, 255, 255, 0.3)",
					size: 4,
					padding: 1,
					stagger: true,
				},
				{
					id: "lines",
					type: "patternLines",
					background: "inherit",
					color: "rgba(255, 255, 255, 0.3)",
					rotation: -45,
					lineWidth: 6,
					spacing: 10,
				},
			]}
			fill={[
				{
					match: {
						id: "ruby",
					},
					id: "dots",
				},
				{
					match: {
						id: "c",
					},
					id: "dots",
				},
				{
					match: {
						id: "go",
					},
					id: "dots",
				},
				{
					match: {
						id: "python",
					},
					id: "dots",
				},
				{
					match: {
						id: "scala",
					},
					id: "lines",
				},
				{
					match: {
						id: "lisp",
					},
					id: "lines",
				},
				{
					match: {
						id: "elixir",
					},
					id: "lines",
				},
				{
					match: {
						id: "javascript",
					},
					id: "lines",
				},
			]}
			legends={[
				{
					anchor: "bottom",
					direction: "row",
					justify: false,
					translateX: 0,
					translateY: 56,
					itemsSpacing: 0,
					itemWidth: 100,
					itemHeight: 18,
					itemTextColor: "#999",
					itemDirection: "left-to-right",
					itemOpacity: 1,
					symbolSize: 18,
					symbolShape: "circle",
					effects: [
						{
							on: "hover",
							style: {
								itemTextColor: "#000",
							},
						},
					],
				},
			]}
		/>
	);
};

export default Charts;
